function incrementWeightLossCounter(weightAbbr){
  totalWeightLost += 1;

  var totalWeightLostArr = totalWeightLost.toString().split("");

  //Build counter html
  var counterHtml = "";
  var i=0;
  for ( i = 0;i < totalWeightLostArr.length;i++){
    //Build number. Add class='head' if first number, 'tail' if the last.
    counterHtml += '<span ' + (i==0?'class="head"':'') + (i==(totalWeightLostArr.length - 1)?'class="tail"':'') + '>' + totalWeightLostArr[i].toString() + '</span>';

    //Add comma immediately after current number if needed
    if ( ( ( totalWeightLostArr.length - (i+1) )%3 == 0 ) && ( i != totalWeightLostArr.length - 1 ) ) {
      counterHtml += '<span class="comma" >,</span> ';
    }
  }

  counterHtml += ' ' + weightAbbr;

  $j('#pounds').html(counterHtml);
};

