function CheckNumeric(e, elementid) {
  var strWork = '0123456789\b\t\f\n\r';
  var blnValidChar = false;         // Valid character flag
  var key;
  if (window.event) {
    key = window.event.keyCode;
  } else if (e.which) {
    key = e.which;
  } else {
    return true;
  }

  if (!blnValidChar) {
    for (var i=0; i < strWork.length; i++) {
      if (key === strWork.charCodeAt(i) || key === 44 || key === 188 || key === 46 || key === 190 || (key >= 37 && key <= 40) || (key >= 96 && key <= 105)) {
        blnValidChar = true;
        break;
      }
    }
  }
  document.getElementById(elementid).focus();
  return blnValidChar;
}

// this calculates energy output according to duration of the exercise.
// if an exercise is not isolated on the page, an index data-attr can be passed in
// so that the associated output field's value will get updated
function calculateEnergyOutput(index) {
  var $outputField = $('#exercise_entry_calories');
  var $exerciseQuantityField = $('#exercise_entry_quantity');
  var $metsField = $('#mets');
  var currentWeight = $('#current_weight').val();
  var roundedValue, exerciseQuantity, mets;

  if (index) {
    $outputField = $('#favorites_' + index + '_calories');
    $exerciseQuantityField = $('#favorites_' + index + '_quantity');
    $metsField = $('#favorites_' + index + '_mets');
  }

  mets = $metsField.val();
  exerciseQuantity = $exerciseQuantityField.val();
  roundedValue = exerciseQuantity * (mets / 60) * currentWeight;

  return MFP.User.getCurrentUser().then(function(user) {
    if (user.unit_preferences.energy === 'kilojoules') {
      roundedValue *= 4.184;
    }

    $outputField.val(Math.round(roundedValue));
  });
}

function RefreshNutrients(){
  var re_del = new RegExp('\\' + $('#delimiter').val(), 'g');
  var re_sep = new RegExp('\\' + $('#separator').val(), 'g');
  var calorieMultiplier = $('#calorie_multiplier').val();
  var calorieTarget = parseInt(document.getElementById('goals_daily_energy_goal').value.replace(re_del,'').replace(re_sep,'.'), 10);
  var protein = parseInt(document.getElementById('goals_protein_ratio').value, 10) / 100;
  var fat = parseInt(document.getElementById('goals_fat_ratio').value, 10) / 100;
  var carb = parseInt(document.getElementById('goals_carb_ratio').value, 10) / 100;

  var gramsProtein = Math.round(calorieTarget / calorieMultiplier * protein / 4);
  var gramsFat = Math.round(calorieTarget / calorieMultiplier * fat / 9);
  var gramsCarbs = Math.round(calorieTarget / calorieMultiplier * carb / 4);

  document.getElementById('protein_target').innerHTML = String(gramsProtein);
  document.getElementById('fat_target').innerHTML = String(gramsFat);
  document.getElementById('carb_target').innerHTML = String(gramsCarbs);
  return;
}

function AdjustProfile(bmr, calorieString){
  var re_del = new RegExp('\\' + $('#delimiter').val(), 'g');
  var re_sep = new RegExp('\\' + $('#separator').val(), 'g');
  var calorieMultiplier = $('#calorie_multiplier').val();
  var weightMultiplier = parseInt($('#weight_multiplier').val(), 10);
  bmr = Math.round(bmr / 10) * 10;
  var calorieTarget = Math.round( parseInt(document.getElementById('goals_daily_energy_goal').value.replace(re_del,'').replace(re_sep,'.'), 10) );
  var calorieDeficit = Math.round((calorieTarget - bmr*calorieMultiplier) * -1);
  var weightloss = calorieString === 'kilojoules' ? Math.round(calorieDeficit / 209.2) / 10 : Math.round(calorieDeficit / 50) / 10;
  weightloss = Math.round(weightloss*10 / weightMultiplier)/10;
  if (Math.floor(weightloss) === weightloss) {
    weightloss += '.0';
  }

  document.getElementById('calorie_target').innerHTML = String(calorieTarget);
  document.getElementById('calorie_deficit').innerHTML = String(calorieDeficit);
  document.getElementById('weight_loss').innerHTML = String(weightloss);
}


function ProteinAdjust(){
  var protein = parseInt(document.getElementById('goals_protein_ratio').value, 10);
  var fat = parseInt(document.getElementById('goals_fat_ratio').value, 10);
  var carb = parseInt(document.getElementById('goals_carb_ratio').value, 10);

  var total = protein + fat + carb;
  if (total !== 100) {
    if (protein + carb < 100) {
      fat = 100 - protein - carb;
    }
    else {
      fat = 5;
      carb = 100 - protein - fat;
    }
  }

  document.getElementById('goals_carb_ratio').value = carb;
  document.getElementById('goals_fat_ratio').value = fat;
  RefreshNutrients();
}

