function timed_field_observer(selector, callback, time_interval) {
  var v = $j(selector).attr('value');
  var tid = setInterval(function() {
    var $el = $j(selector);
    if ($el.length === 0) {
      /* clearInterval(tid); */
      return;
    }

    var nv = $el.attr('value');

    if (nv === v) {
      return;
    }

    v = nv;
    callback.apply($el[0], arguments);
  }, 25 || time_interval);
  return tid;
}


function change_field_observer(selector, callback) {
  var $el = $j(selector);
  var type = $el.attr('type');
  if (type) {
    type = type.toLowerCase();
  }
  if (type === 'checkbox' || type === 'radio') {
    $el.bind('click', callback);
  }
  else {
    $el.bind('change', callback);
  }
}

