function TagField(options) {
  this.options = { max_tags: 99 };
  this.active_input = $j(options.active_input_selector);
  this.storage_input = $j(options.storage_input_selector);
  var me = this;
  if (this.active_input.length) {
    this.active_input.bind('keyup', function() {
      me.onKeyup.apply(me, arguments);
    });

    this.active_input.bind('blur', function() {
      me.onBlur.apply(me, arguments);
    });

    this.ul = this.active_input.parents('ul:first');
    if (this.ul.length) {
      this.ul.bind('click', function() {
        me.focus_input.apply(me, arguments);
      });
    }
  }

  this.form = $j(options.submission_form_selector);
  this.form.bind('submit', function(){
    me.onSubmit.apply(me, arguments);
  });

  this.attach_remove_tags();
}

TagField.prototype.onKeyup = function(event) {
  if (event.keyCode == 188) { // comma pressed
    this.add_tag($j.trim(this.active_input.attr('value')).replace(',',''));
    if (this.reached_tag_limit()) {
      this.hide_input();
    }
    this.active_input.attr('value', '');
  }
}

TagField.prototype.onBlur = function() {
  if (this.active_input.attr('value') != '') {
    this.add_tag($j.trim(this.active_input.attr('value')));
    this.active_input.attr('value', '');
  }
}

TagField.prototype.onSubmit = function(e) {
  e.preventDefault();
  this.active_input.hide();
  if (this.active_input.value != '') {
    this.add_tag($j.trim(this.active_input.attr('value')));
  }

  this.active_input.attr('value', '');
  this.form[0].submit();
}

TagField.prototype.reached_tag_limit = function() {
  var val = this.storage_input.attr('value');
  return (val != '' && val.split(',').length >= this.options.max_tags);
}

TagField.prototype.hide_input = function() {
  this.active_input.parent().hide();
}

TagField.prototype.show_input = function() {
  this.active_input.parent().show();
}

TagField.prototype.add_tag = function(tag_text) {
  if (tag_text == '') {
    return;
  }
  if (this.reached_tag_limit()) {
    return;
  }
  var li = $j('<li class="tag">'+tag_text+'</li>');
  li.bind('mouseover', function() { $j(this).addClass('hover') });
  li.bind('mouseout', function(){ $j(this).removeClass('hover') });

  var a = $j('<a href="#" class="remove"></a>');
  var me = this;
  a.bind('click', function() {
    me.remove_tag.apply(me, arguments);
  });
  a.appendTo(li);
  var existing_tags = this.ul.find('li.tag');
  if (existing_tags.length) {
    li.insertAfter(existing_tags.last());
  } else {
    this.ul.prepend(li);
  }

  var tag = tag_text.toLowerCase();
  var val = $j.trim(this.storage_input.attr('value'));
  if (val == '') {
    val = tag;
  } else {
    val +=  ',' + tag;
  }
  this.storage_input.attr('value', val);
}

TagField.prototype.remove_tag = function(event) {
  event.preventDefault();
  var text = $j(event.target).parent().text();
  text = $j.trim(text).toLowerCase();
  var field = this.storage_input;
  var oldVals = field.attr('value').split(',');
  var newVals = [];
  for (var i = 0, l = oldVals.length; i < l; ++i) {
    var val = oldVals[i];
    if (val == text) {
      continue;
    }
    newVals.push(val);
  }
  field.attr('value', newVals.join(','));

  $j(event.target).parent().remove();

  if (!this.reached_tag_limit()) {
    this.show_input();
  }
  this.active_input.focus();
}

TagField.prototype.attach_remove_tags = function() {
  var me = this;
  function remove(){
    me.remove_tag.apply(me, arguments);
  }

  // when end-user is not logged in, tagging is not available
  // hence no need to remove event handlers
  if (typeof this.ul != 'undefined') {
    this.ul.find('a.remove').each(function() {
      $j(this).bind('click', remove);
    });
  }
}

TagField.prototype.focus_input = function() {
  this.active_input.focus();
}
