function toggleUnitsNew() {
  var new_unit = "";
  var weight_unit = "";
  var feet_unit = "";
  var inches_unit = "";
  var units_toggle = "";
  var height = 0.0;
  var new_height = 0.0;
  if ($j('#preference_weight_unit').val() == 'pounds') {
    new_unit = "kilograms";
    weight_unit = "kg";
    feet_unit = "meters";
    inches_unit = "cm";
    units_toggle = "switch to english (lbs)";
    if (($j('#weight_value').val() * 1.0) > 0.0) {
      $j('#weight_value').val(Math.round($j('#weight_value').val() / 2.20462262 * 10) / 10.0);
    }
    if (($j('#profile_goal_weight').val() * 1.0) > 0.0) {
      $j('#profile_goal_weight').val(Math.round($j('#profile_goal_weight').val() / 2.20462262 * 10) / 10.0);
    }
    height = $j('#profile_height_big').val() * 12.0 + $j('#profile_height_small').val() * 1.0;
    new_height = height * 2.54001;
    $j('#profile_height_big').val(Math.floor(new_height / 100));
    $j('#profile_height_small').val(Math.round(new_height % 100));
  } else {
    new_unit = "pounds";
    weight_unit = "lbs";
    feet_unit = "feet";
    inches_unit = "inches";
    units_toggle = "switch to metric (kg)";
    if (($j('#weight_value').val() * 1.0) > 0.0) {
      $j('#weight_value').val(Math.round($j('#weight_value').val() * 2.20462262 * 10) / 10.0);
    }
    if (($j('#profile_goal_weight').val() * 1.0) > 0.0) {
      $j('#profile_goal_weight').val(Math.round($j('#profile_goal_weight').val() * 2.20462262 * 10) / 10.0);
    }
    height = $j('#profile_height_big').val() * 100.0 + $j('#profile_height_small').val() * 1.0;
    new_height = height * 0.393700787;
    $j('#profile_height_big').val(Math.floor(new_height / 12));
    $j('#profile_height_small').val(Math.round(new_height % 12));
  }
  $j('#preference_weight_unit').val(new_unit);
  $j('#preference_feet_unit').val(feet_unit);
  $j('#preference_inches_unit').val(inches_unit);
  $j('.weight_unit').html(weight_unit);
  $j('#feet_unit').html(feet_unit);
  $j('#inches_unit').html(inches_unit);
  $j('#units_toggle').html(units_toggle);
}


