function amzn_ads_wrapper(slotId, alreadyTested){
  var amazonSlots = document.amzn_slots;

  if (!amazonSlots) {
    if (!alreadyTested) {
      setTimeout(function(){
        amzn_ads_wrapper(slotId, true);
      }, 100);
    } else {
      googletag.cmd.push(function() {
        googletag.display(slotId);
      });
    }
    return;
  }

  var googletagCallback = function() {
    var slot, slotName;
    var amazonSlots = document.amzn_slots;

    if (!amazonSlots) {
      return;
    }

    for(slot in amazonSlots) {
      if (!amazonSlots.hasOwnProperty(slot)) {
        continue;
      }

      slotName = amazonSlots[slot];
      set_ad_targeting(slotName, '1');
    }
  };

  googletag.cmd.push(googletagCallback);
  googletag.cmd.push(function() {
    googletag.display(slotId);
  });
}

function set_ad_targeting(targeting_name, targeting_value){
  if (googletag && ('pubads' in googletag)) {
    googletag.pubads().setTargeting(targeting_name, targeting_value);
  }
}
